function AddToCart(n,t,i,r){$.ajax({type:"GET",contentType:"application/json; charset=utf-8",url:"/Cart/AddToCart/"+n+"/"+i+"/"+r,success:function(n){n.status=="success"&&$("#cartCounter").text(n.count)}})}function deleteItem(n){n>0&&$.ajax({type:"GET",url:"/Cart/DeleteItem/"+n,success:function(n){n>0&&location.reload()}})}function updateQuantity(n,t,i){(t>=1&&i==1||t>1&&i==-1)&&$.ajax({url:"/Cart/UpdateQuantity/"+n+"/"+i,type:"GET",success:function(n){n>0&&location.reload()}})}$(document).ready(function(){$.ajax({type:"GET",url:"/Cart/GetCartCount",success:function(n){$("#cartCounter").text(n)}})});